% Exercise 1 a)% standard k-means% Init[a,b,c,d,X] = initClusters();%Plot raw clustersfigure(1)plotClusters(a,b,c,d)title("std=0.5, n=100 (1 a)")saveas (1, "data_1a.png");% Plot how the k-means look like[assignments, centroids, mse] = kMeans(X,4);XAssigned = horzcat(X, assignments);figure(2)plotKMeans(XAssigned, centroids)title("Cluster (random initialized), std=0.5, L2-Norm (1a)")saveas (2,"cluster_1a.png");# Plot the error per Iterationfigure(3)plot(mse)title("Error per Init  (random initialized), std=0.5, L2-Norm (1a)")saveas (3,"errorPerInit_1a.png");% Iterate 50 Random Initializationsfor i = 1:50  % New Initialization!  [a,b,c,d,X] = initClusters();  % Save all mse  [assignments, centroids, mse] = kMeans(X,4);  % Add mse of final clusters  msePerInitialization(i) = mse(length(mse));  numberOfIterations(i) = length(mse);endforfigure(4)plot(msePerInitialization)title("Mean Squared Errors (Task 1a)")saveas (4,"mse_1a.png");% Plot number of iterationsfigure(5)plot(numberOfIterations)title("Num of Iterations (Task 1a)")saveas (5,"iter_1a.png");%print meansmean(numberOfIterations)mean(msePerInitialization)