% Exercise 1 a)% standard k-means[a,b,c,d,X] = initClusters();title("std=0.5, n=100 (4 clusters)")[assignments, centroids, error] = kMeans(X,4);XAssigned = horzcat(X, assignments);plotKMeans(XAssigned, centroids)title("K-Means (random initialized), std=0.5, L2-Norm")plot(error)title("K-Means (random initialized), std=0.5, L2-Norm")for i = 1:50  [assignments, centroids, error] = kMeans(X,4);  errors(i) = mean(error);endforerrors'mean(errors)