% Exercise 1 b)% randperm[a,b,c,d,X] = initClusters();title("std=0.5, n=100 (4 clusters)")% Get random centroids from the datasetcentroidRows = randperm(size(X)(1));initCentroids = X(centroidRows(1:size(X)), :);% Plot[assignments, centroids, error] = kMeans(X,4,initCentroids);XAssigned = horzcat(X, assignments);plotKMeans(XAssigned, centroids)title("K-Means (randperm), std=0.5, L2-Norm")% Plot Errorplot(error)title("K-Means (randperm), std=0.5, L2-Norm")for i = 1:50  % New centroids!  centroidRows = randperm(size(X)(1));  initCentroids = X(centroidRows(1:4), :);    [assignments, centroids, error] = kMeans(X,4,initCentroids);  errors(i) = mean(error);endforerrors'mean(error)